import React, { useState } from 'react';
import AddTransaction from './AddTransaction';
import TransactionsList from './TransactionsList';

const ComptePage = () => {
  const [selectedCompteId, setSelectedCompteId] = useState(null);

  return (
    <div>
      <h1>Gestion des Comptes et Transactions</h1>
      <AddTransaction />
      <hr />
      <div>
        <h2>Afficher les Transactions par Compte</h2>
        <label htmlFor="compteSelect">SÃ©lectionner un compte :</label>
        <select
          id="compteSelect"
          onChange={(e) => setSelectedCompteId(e.target.value)}
        >
          <option value="">Choisir un compte</option>
          {/* Ajoutez les options pour les comptes ici */}
        </select>
        {selectedCompteId && <TransactionsList compteId={selectedCompteId} />}
      </div>
    </div>
  );
};

export default ComptePage;
