import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { gql } from 'graphql-tag';

// Mutation pour ajouter une transaction
const ADD_TRANSACTION = gql`
  mutation AddTransaction($transactionRequest: TransactionInput!) {
    addTransaction(transactionRequest: $transactionRequest) {
      id
      montant
      date
      type
      compte {
        id
        solde
      }
    }
  }
`;

const AddTransaction = () => {
  const [montant, setMontant] = useState('');
  const [date, setDate] = useState('');
  const [type, setType] = useState('DEPOSIT');
  const [compteId, setCompteId] = useState('');
  const [addTransaction, { loading, error }] = useMutation(ADD_TRANSACTION);

  const handleSubmit = (e) => {
    e.preventDefault();
    addTransaction({
      variables: {
        transactionRequest: {
          montant: parseFloat(montant),
          date,
          type,
          compteId: parseInt(compteId),
        },
      },
    })
    .then(response => {
      console.log("Transaction added", response);
      // Vous pouvez ajouter un message de succès ou réinitialiser les champs du formulaire
    })
    .catch(err => {
      console.error("Error adding transaction:", err);
    });
  };

  return (
    <div>
      <h2>Ajouter une Transaction</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="compteId">ID du Compte:</label>
          <input
            type="number"
            id="compteId"
            value={compteId}
            onChange={(e) => setCompteId(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="montant">Montant:</label>
          <input
            type="number"
            id="montant"
            value={montant}
            onChange={(e) => setMontant(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="type">Type de Transaction:</label>
          <select
            id="type"
            value={type}
            onChange={(e) => setType(e.target.value)}
          >
            <option value="DEPOT">Dépôt</option>
            <option value="RETRAIT">Retrait</option>
          </select>
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "En cours..." : "Ajouter Transaction"}
        </button>
      </form>
      {error && <p style={{ color: 'red' }}>Erreur: {error.message}</p>}
    </div>
  );
};

export default AddTransaction;
