import React, { useState, useEffect } from 'react';
import { gql, useQuery } from '@apollo/client';

// GraphQL Query pour obtenir toutes les transactions d'un compte
const GET_TRANSACTIONS = gql`
  query compteTransactions($compteId: ID!) {
    compteTransactions(compteId: $compteId) {
      id
      montant
      date
      type
    }
  }
`;

const TransactionList = ({ compteId }) => {
  const { data, loading, error } = useQuery(GET_TRANSACTIONS, {
    variables: { compteId },
  });

  if (loading) return <p>Chargement des transactions...</p>;
  if (error) return <p>Erreur lors de la récupération des transactions: {error.message}</p>;

  return (
    <div>
      <h2>Transactions du Compte {compteId}</h2>
      <ul>
        {data.compteTransactions.map((transaction) => (
          <li key={transaction.id}>
            {transaction.type} de {transaction.montant}€ le {transaction.date}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionList;
