import React, { useState } from 'react';
import ComptesList from './components/ComptesList';
import AddCompte from './components/AddCompte';
import AddTransaction from './components/AddTransaction'; // Import du composant d'ajout de transactions
import TransactionsList from './components/TransactionsList';

function App() {
  const [selectedCompteId, setSelectedCompteId] = useState(null);

  // Fonction pour changer le compte sélectionné
  const handleCompteSelect = (compteId) => {
    setSelectedCompteId(compteId);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Gestion des Comptes et Transactions</h1>

      {/* Ajouter un nouveau compte */}
      <AddCompte />

      {/* Ajouter une transaction */}
      <h2>Ajouter une Transaction</h2>
      <AddTransaction />

      {/* Liste des comptes */}
      <h2>Liste des Comptes</h2>
      <ComptesList onSelectCompte={handleCompteSelect} />

      {/* Afficher les transactions d'un compte sélectionné */}
      {selectedCompteId && (
        <div style={{ marginTop: '20px' }}>
          <h2>Transactions du Compte ID: {selectedCompteId}</h2>
          <TransactionsList compteId={selectedCompteId} />
        </div>
      )}
    </div>
  );
}

export default App;
